CREATE TABLE IF NOT EXISTS "post_to_tags" ("post_id" INTEGER NOT NULL, "tag_id" INTEGER NOT NULL, PRIMARY KEY ("post_id", "tag_id"));
CREATE TABLE IF NOT EXISTS "post_to_posts" ("source_post_id" INTEGER NOT NULL, "target_post_id" INTEGER NOT NULL, PRIMARY KEY ("source_post_id", "target_post_id"));
CREATE TABLE IF NOT EXISTS "post_to_projects" ("post_id" INTEGER NOT NULL, "project_id" INTEGER NOT NULL, PRIMARY KEY ("post_id", "project_id"));
CREATE TABLE IF NOT EXISTS "project_to_tags" ("project_id" INTEGER NOT NULL, "tag_id" INTEGER NOT NULL, PRIMARY KEY ("project_id", "tag_id"));
CREATE TABLE IF NOT EXISTS "project_to_projects" ("source_project_id" INTEGER NOT NULL, "target_project_id" INTEGER NOT NULL, PRIMARY KEY ("source_project_id", "target_project_id"));
CREATE TABLE IF NOT EXISTS "tag_to_tags" ("source_tag_id" INTEGER NOT NULL, "target_tag_id" INTEGER NOT NULL, PRIMARY KEY ("source_tag_id", "target_tag_id"));
CREATE TABLE IF NOT EXISTS "posts" ("id" INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, "x" DOUBLE PRECISION, "y" DOUBLE PRECISION, "z" DOUBLE PRECISION, "title" VARCHAR, "slug" VARCHAR, "description" VARCHAR, "content" VARCHAR, "banner_path" VARCHAR, "created_at" VARCHAR, "tag_slugs" VARCHAR, "post_slugs" VARCHAR, "project_slugs" VARCHAR, UNIQUE ("slug"));
CREATE TABLE IF NOT EXISTS "tags" ("id" INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, "x" DOUBLE PRECISION, "y" DOUBLE PRECISION, "z" DOUBLE PRECISION, "title" VARCHAR, "slug" VARCHAR, "description" VARCHAR, "content" VARCHAR, "banner_path" VARCHAR, "icon" VARCHAR, "created_at" VARCHAR, "tag_slugs" VARCHAR, "post_slugs" VARCHAR, "project_slugs" VARCHAR, UNIQUE ("slug"));
CREATE TABLE IF NOT EXISTS "projects" ("id" INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, "x" DOUBLE PRECISION, "y" DOUBLE PRECISION, "z" DOUBLE PRECISION, "title" VARCHAR, "slug" VARCHAR, "description" VARCHAR, "content" VARCHAR, "banner_path" VARCHAR, "created_at" VARCHAR, "tag_slugs" VARCHAR, "post_slugs" VARCHAR, "project_slugs" VARCHAR, UNIQUE ("slug"));
CREATE TABLE IF NOT EXISTS "caches" ("id" INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, "path" VARCHAR, "hashed" VARCHAR, "x" DOUBLE PRECISION, "y" DOUBLE PRECISION, "z" DOUBLE PRECISION, UNIQUE ("path"), UNIQUE ("hashed"));
